import { Server } from './server.js';
import { ComponentServer } from './component-server.js';
import { Route, NestedRoute } from '../types/types.js';
import { ComponentRoute, NestedComponentRoute } from '../types/component-route.js';
import './base-server.js';
import '../utils/dependency-manager.js';
import '../types/route.js';

/**
 * 服务器工厂类
 * 用于创建和管理不同类型的服务器
 */
declare class ServerFactory {
    private servers;
    /**
     * 创建标准REST API服务器
     */
    createRestServer(routes: (Route | NestedRoute)[]): Server;
    /**
     * 创建组件服务器
     */
    createComponentServer(routes: (ComponentRoute | NestedComponentRoute)[]): ComponentServer;
    /**
     * 获取指定类型的服务器
     */
    getServer(type: "rest" | "component"): Server | ComponentServer | undefined;
    /**
     * 获取所有服务器
     */
    getAllServers(): Map<string, Server | ComponentServer>;
    /**
     * 移除指定类型的服务器
     */
    removeServer(type: string): boolean;
    /**
     * 清除所有服务器
     */
    clearServers(): void;
    /**
     * 获取服务器状态信息
     */
    getServerStatus(): Record<string, {
        type: string;
        routes: number;
    }>;
}

export { ServerFactory };
